import React, { useState } from 'react';
import './App.css';
import image1 from '/src/test_images/beach.jpg';
import image2 from '/src/test_images/farm.jpeg';
import image3 from '/src/test_images/amsterdam.jpeg';
import image4 from '/test_images/gkotzostrena.jpg';
import image5 from '/test_images/hair_salon.jpeg';
import image6 from '/test_images/london.jpg';
import image7 from '/test_images/palia_spitia.jpeg';
import image8 from '/test_images/street2.jpeg';
import image9 from '/test_images/street3.jpg.jpeg';
import image10 from '/test_images/street.jpeg';
import image11 from '/test_images/trena2.jpg';
import image12 from '/test_images/trena.jpg';
import image13 from '/test_images/tzalliaschorus.jpg';
import image14 from '/test_images/tzalliasdromos.jpg';
import image15 from '/test_images/tzallias.jpg';

const App = () => {
    const [images, setImages] = useState([
        { id: 1, src: image1, isLiked: false },
        { id: 2, src: image2, isLiked: false },
        { id: 3, src: image3, isLiked: false },
        { id: 4, src: image4, isLiked: false },
        { id: 5, src: image5, isLiked: false },
        { id: 6, src: image6, isLiked: false },
        { id: 7, src: image7, isLiked: false },
        { id: 8, src: image8, isLiked: false },
        { id: 9, src: image9, isLiked: false },
        { id: 10, src: image10, isLiked: false },
        { id: 11, src: image11, isLiked: false },
        { id: 12, src: image12, isLiked: false },
        { id: 13, src: image13, isLiked: false },
        { id: 14, src: image14, isLiked: false },
        { id: 15, src: image15, isLiked: false },
    ]);
    const [selectedImage, setSelectedImage] = useState(null);

    const handleLike = (id) => {
        setImages((prevImages) =>
            prevImages.map((image) =>
                image.id === id ? { ...image, isLiked: !image.isLiked } : image
            )
        );
    };

    const handleSelect = (id) => {
        setSelectedImage(images.find((image) => image.id === id));
    };

    const handleClose = () => {
        setSelectedImage(null);
    };

    return (
        <div className="app">
            {selectedImage ? (
                <div className="selected-image">
                    <button onClick={handleClose}>‚Üê</button>
                    <img src={selectedImage.src} alt="" />
                </div>
            ) : (
                <div className="image-grid">
                    {images.map((image) => (
                        <div
                            className="image-item"
                            key={image.id}
                            onClick={() => handleSelect(image.id)}
                        >
                            <img src={image.src} alt="" />
                            <div className="image-item-info">
                                <button onClick={() => handleLike(image.id)}>
                                    {image.isLiked ? "‚ù§Ô∏è" : "ü§ç"}
                                </button>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default App;
