import React, { useState } from 'react';
import './App.css';

const initialImages = [
    { id: 1, url: '/test_images/beach.jpg', isLiked: false },
    { id: 2, url: '/test_images/farm.jpeg', isLiked: false },
    { id: 3, url: '/test_images/amsterdam.jpeg', isLiked: false },
    { id: 4, url: '/test_images/gkotzostrena.jpg', isLiked: false },
    { id: 5, url: '/test_images/hair_salon.jpeg', isLiked: false },
    { id: 6, url: '/test_images/london.jpg', isLiked: false },
    { id: 7, url: '/test_images/palia_spitia.jpeg', isLiked: false },
    { id: 8, url: '/test_images/street2.jpeg', isLiked: false },
    { id: 9, url: '/test_images/street3.jpg.jpeg', isLiked: false },
    { id: 10, url: '/test_images/street.jpeg', isLiked: false },
    { id: 11, url: '/test_images/trena2.jpg', isLiked: false },
    { id: 12, url: '/test_images/trena.jpg', isLiked: false },
    { id: 13, url: '/test_images/tzalliaschorus.jpg', isLiked: false },
    { id: 14, url: '/test_images/tzalliasdromos.jpg', isLiked: false },
    { id: 15, url: '/test_images/tzallias.jpg', isLiked: false },
];

const App = () => {
    const [images, setImages] = useState(initialImages);
    const [selectedImage, setSelectedImage] = useState(null);

    const handleLike = (id) => {
        setImages((prevImages) =>
            prevImages.map((image) =>
                image.id === id ? { ...image, isLiked: !image.isLiked } : image
            )
        );
    };

    const handleSelect = (id) => {
        setSelectedImage(images.find((image) => image.id === id));
    };

    const handleClose = () => {
        setSelectedImage(null);
    };

    return (
        <div className="app">
            {selectedImage ? (
                <div className="selected-image">
                    <button onClick={handleClose}>‚Üê</button>
                    <img src={selectedImage.url} alt="" />
                </div>
            ) : (
                <div className="image-grid">
                    {images.map((image) => (
                        <div
                            className="image-item"
                            key={image.id}
                            onClick={() => handleSelect(image.id)}
                        >
                            <img src={image.url} alt="" />
                            <div className="image-item-info">
                                <button onClick={(e) => {e.stopPropagation(); handleLike(image.id)}}>
                                    {image.isLiked ? "‚ù§Ô∏è" : "ü§ç"}
                                </button>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default App;
