import React, { useState } from 'react';
import './App.css';

// Sample data
const initialImages = [
    { id: 1, url: 'image1.jpg', likes: 0 },
    { id: 2, url: 'image2.jpg', likes: 0 },
    { id: 3, url: 'image3.jpg', likes: 0 },
    // add more image objects...
];

function App() {
    const [images, setImages] = useState(initialImages);

    const handleLike = (id) => {
        const newImages = images.map(image =>
            image.id === id ? { ...image, likes: image.likes + 1 } : image
        );
        setImages(newImages);
    };

    return (
        <div className="App">
            <div className="masonry">
                {images.map(image => (
                    <div key={image.id} className="image-item">
                        <img src={image.url} alt="" />
                        <div className="image-item-info">
                            <button onClick={() => handleLike(image.id)}>
                                Like
                            </button>
                            <span>{image.likes} likes</span>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default App;
