import React, { useState } from 'react';
import './App.css';

const initialImages = [
    // Your images
];

const App = () => {
    const [images, setImages] = useState(initialImages);
    const [selectedImage, setSelectedImage] = useState(null);

    const handleLike = (id) => {
        setImages((prevImages) =>
            prevImages.map((image) =>
                image.id === id ? { ...image, isLiked: !image.isLiked } : image
            )
        );
    };

    const handleSelect = (id) => {
        setSelectedImage(images.find((image) => image.id === id));
    };

    const handleClose = () => {
        setSelectedImage(null);
    };

    return (
        <div className="app">
            <header>My Pintrest Clone</header>
            <div className="image-grid">
                {images.map((image) => (
                    <div
                        className={`image-item ${selectedImage?.id === image.id ? 'selected' : ''}`}
                        key={image.id}
                        onClick={() => handleSelect(image.id)}
                    >
                        <img src={image.url} alt="" />
                        <div className="image-item-info">
                            <button onClick={(e) => {e.stopPropagation(); handleLike(image.id)}}>
                                {image.isLiked ? "‚ù§Ô∏è" : "ü§ç"}
                            </button>
                        </div>
                    </div>
                ))}
            </div>
            {selectedImage && 
                <button className="close-button" onClick={handleClose}>‚Üê</button>}
        </div>
    );
};

export default App;
